import React, {Component} from 'react';
import { BrowserRouter as Router, Route, Switch} from 'react-router-dom'
import {Home} from './Home';
import {About} from './About';
import {Contact} from './Contact';
import {NoMatch} from './NoMatch';
import { Layout } from './components/Layout';
import { NavigationBar} from './components/NavigationBar';
import { Jumbotron } from './components/Jumbotron';
import { HashLink as Link } from 'react-router-hash-link';
import { ResumePDF } from './components/Resume';
import styled from 'styled-components';
import { Separator } from './components/SeparatorComponent';

const Styles = styled.div`
  #contact, #about, #resume {
    height : 100px;
  }

  #resumeContent {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
  }


  #navBar {
    height: 60px;
    width: 100%;
  }

  #projects {
    padding: 5vh;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: #343A40;
  }
`;

class App extends Component {
  render() {
    return (
      // creates a wrapper
      <React.Fragment> 
        <Router>
            <div id="navBar">
              <NavigationBar />
            </div>
            <Switch>
              <Link to="/#home"/>
              <Link to="/#contact"/>
              <Link to="/#resume"/>
              <Link to="/#projects"/>
              <Route component={NoMatch} />
              <Link to="/#anchor"></Link>
            </Switch>
          </Router>
        <Styles>
          <div id = "container">
          <div id = "home"/>
          <div >
            <Home />
          </div>
          <div id="projects">
            <Parallax
                strength={200}
                renderLayer={percentage => (
                  <div>
                    <div
                      style={{
                        position: "absolute",
                        background: `rgba(242, 141, 141, ${percentage * 1})`,
                        left: "50%",
                        top: "50%",
                        borderRadius: "50%",
                        transform: "translate(-50%,-50%)",
                        width: percentage * 500,
                        height: percentage * 500
                      }}
                    />
                  </div>
      )}
            >
      <div>
        <div>
            <video width="50%" height="50%" controls>
                <source src={require('./assets/antsDemo.mp4')} type="video/mp4"></source>
            </video>
        </div>
      </div>
    </Parallax>
          </div>

          <div id="resume"/>
          <div id="resumeContent">

          <embed src={require('./assets/Resume.pdf')} width="50%" height="1000px"></embed>

          </div>

          <div id="contact"/>
          <div >
            <Contact />
          </div>
          </div>
        </Styles>
      </React.Fragment>
    );
  }

}

export default App;
