import React from 'react';
import { Nav, Navbar } from 'react-bootstrap';
import { NavHashLink as Link } from 'react-router-hash-link';
import styled from 'styled-components';


const Styles = styled.div`
    .navbar {
        background-color: #2685BF;
        height: 60px;
        position: fixed;
    }

    .navbar-brand{
        color: #BBE8F2;
        padding: 10px;
        border-radius: 5px;

        &:hover {
            color: #fff;
            text-decoration: none;
            background-color: #5FB6D9;
        }

    }
    .links {
        color: #BBE8F2;
        padding: 10px;
        border-radius: 5px;
    }

    .links:hover {
        text-decoration: none;
        background-color: #5FB6D9;
        color: #fff;
    }

    .here {
        text-decoration: none;
        background-color: #5FB6D9;
        color: #fff;
    }


`;

export const NavigationBar = () => (
    <Styles>
        <Navbar expand="lg" fixed="top">
            <Navbar.Brand href="/#home" >Andy Fang</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
                <Nav className="ml-auto">
                    <Nav.Item>
                        <Link strict to="/#about" className="links" smooth="true" activeClassName="here">About</Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Link exact to="/#resume" className="links" smooth="true" activeStyle={{backgroundColor:'#5FB6D9', color:'#fff'}}>Resume</Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Link exact to="/#contact" className="links" smooth="true" activeClassName="here">Contact</Link>
                    </Nav.Item>
                </Nav>
            </Navbar.Collapse>
        </Navbar>
    </Styles>
)
